// Importurile existente
import React, { useState, useEffect } from 'react'; 
import axios from 'axios';
import './App.css';

const ProductManagementApp = () => {
  const [products, setProducts] = useState([]);
  const [cart, setCart] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  
  // State nou pentru gestionarea formularului de review
  const [reviewForm, setReviewForm] = useState({
    productId: null,
    nickname: '',
    rating: 5,
    comment: ''
  });
  
  // State pentru a afișa/ascunde formularul
  const [showReviewForm, setShowReviewForm] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [productsResponse, cartResponse] = await Promise.all([
          axios.get('http://localhost:5000/api/products'),
          axios.get('http://localhost:5000/api/cart')
        ]);

        setProducts(productsResponse.data);
        setCart(cartResponse.data);
        setLoading(false);
      } catch (err) {
        setError('Failed to fetch data');
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const addToCart = async (product) => {
    try {
      const response = await axios.post('http://localhost:5000/api/cart', { product });
      setCart(response.data);
    } catch (err) {
      setError('Failed to add product to cart');
    }
  };

  const removeFromCart = async (productId) => {
    try {
      const response = await axios.delete(`http://localhost:5000/api/cart/${productId}`);
      setCart(response.data);
    } catch (err) {
      setError('Failed to remove product from cart');
    }
  };
  
  // Funcție pentru gestionarea începerii unei recenzii
  const startReview = (productId) => {
    setReviewForm({ ...reviewForm, productId });
    setShowReviewForm(true);
  };
  
  // Funcție pentru gestionarea modificărilor în formularul de recenzii
  const handleReviewChange = (e) => {
    const { name, value } = e.target;
    setReviewForm({ ...reviewForm, [name]: value });
  };
  
  // Funcție pentru trimiterea recenziei
  const submitReview = async (e) => {
    e.preventDefault();
    try {
      await axios.post(
        `http://localhost:5000/api/products/${reviewForm.productId}/reviews`, 
        reviewForm
      );
      
      const productsResponse = await axios.get('http://localhost:5000/api/products');
      setProducts(productsResponse.data);
      setReviewForm({ productId: null, nickname: '', rating: 5, comment: '' });
      setShowReviewForm(false);
    } catch (err) {
      setError('Failed to submit review');
    }
  };

  if (loading) return <div className="loading">Loading...</div>;
  if (error) return <div className="error">Error: {error}</div>;

  return (
    <div className="container">
      <h1>Product Management App</h1>
      
      {showReviewForm && (
        <div className="review-form-container">
          <div className="review-form">
            <h3>Add Your Review</h3>
            <form onSubmit={submitReview}>
              <input type="text" name="nickname" value={reviewForm.nickname} onChange={handleReviewChange} placeholder="Anonymous" />
              <select name="rating" value={reviewForm.rating} onChange={handleReviewChange}>
                {[5, 4, 3, 2, 1].map(num => <option key={num} value={num}>{num} Stars</option>)}
              </select>
              <textarea name="comment" value={reviewForm.comment} onChange={handleReviewChange} placeholder="Share your thoughts" />
              <button type="submit">Submit Review</button>
              <button type="button" onClick={() => setShowReviewForm(false)}>Cancel</button>
            </form>
          </div>
        </div>
      )}
      
      <div className="products-section">
        <h2>Available Products</h2>
        <div className="product-grid">
          {products.map(product => (
            <div key={product.id} className="product-card">
              <h3>{product.name}</h3>
              <p>{product.description}</p>
              <p>Price: ${product.price} (€{product.euroPrice})</p>
              <button onClick={() => addToCart(product)}>Add to Cart</button>
              
              <div className="reviews">
                <h4>Reviews</h4>
                {product.reviews && product.reviews.length > 0 ? (
                  product.reviews.map(review => (
                    <div key={review.id} className="review">
                      <span className="reviewer">{review.nickname || "Anonymous"}</span>
                      <span className="rating">
                        {Array(review.rating || 5).fill('★').join('')}
                        {Array(5 - (review.rating || 5)).fill('☆').join('')}
                      </span>
                      <p className="review-date">{review.date ? new Date(review.date).toLocaleDateString() : "No date available"}</p>
                      <p className="review-comment">{review.comment}</p>
                    </div>
                  ))
                ) : <p>No reviews yet. Be the first to review!</p>}
                <button onClick={() => startReview(product.id)}>Write a Review</button>
              </div>
            </div>
          ))}
        </div>
      </div>
      
      <div className="cart-section">
        <h2>Shopping Cart</h2>
        {cart.length === 0 ? <p>Your cart is empty</p> : (
          cart.map(item => (
            <div key={item.id} className="cart-item">
              <span>{item.name}</span>
              <span>Quantity: {item.quantity}</span>
              <button onClick={() => removeFromCart(item.id)}>Remove</button>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default ProductManagementApp;
